
var Q = require("../qq");

module.exports = {
    "test queue": function (assert, done) {
        var queue = Q.Queue();
        queue.put(1);
        queue.get()
        .then(function (one) {
            assert.equal(one, 1, "got one");
        })
        .fail(function () {
            assert.ok(false, 'exception');
        })
        .fin(done)
    }
}

if (require.main === module) 
    require("test").run(module.exports);

